import React, { useEffect, useState } from 'react';
import { Field, reduxForm } from 'redux-form';
import axios from 'axios';


// Form validation part :

const validate = values => {
  const errors = {}
  if (!values.firstName) {
    errors.firstName = 'Required First Name'
  } else if (values.firstName.length > 15) {
    errors.firstName = 'Must be 15 characters or less'
  }
  if (!values.lastName) {
    errors.lastName = 'Required Last Name'
  } else if (values.lastName.length > 15) {
    errors.lastName = 'Must be 15 characters or less'
  }
  if (!values.email) {
    errors.email = 'Required Email'
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = 'Invalid email address'
  }
  if (!values.password) {
    errors.password = 'Required password';
  }
  if (!values.gender){
    errors.gender = 'Required to Choose any one';
}
  return errors
}

const data = [
  {
    name: "test1",
    result: "pass"
  },
  {
    name: "test2",
    result: "pass"
  },
  {
    name: "test3",
    result: "pass"
  },
  {
    name: "test4",
    result: "pass"
  },
  {
    name: "test5",
    result: "pass"
  }
];



// Component validation defined of Redux-form

const TextInput = ({
  input,
  label,
  type,
  meta: { touched, error, warning }}) =>
  <div><label>{label}</label>
  <div>
  <input {...input} placeholder={label} type={type} />
  {touched &&((error && <span className="error">{error} </span>) || (warning &&
  <span className="warning">{warning}</span>))}
  </div>
  </div>

// Redux Form submitting 

const Submit = async (values) => {
    let data = {
        user: {
            type: values.type,
            firstName: values.firstName,
            lastName: values.lastName,
            email: values.email,
            password: values.password,
            gender: values.gender,
        },
    }
           console.log("data->>>>>",data);
  axios
      .post(`https://jsonplaceholder.typicode.com/users`, { data })
      .then(res => {
      console.log("response->>>>>>>",res);
      console.log("response-data->>>>>>>",res.data);
    });
}

  const SimpleForm = props => {
  const { handleSubmit, pristine, reset, submitting } = props

  // Return part of Jsx Element : 

return (
    <form onSubmit={handleSubmit((values) => Submit(values))}>

    <div className="row">
         <Field
            name="firstName"
            type="text"
            component={TextInput}
            label="First Name"
         />
      </div>

      <div className="row">
            <Field
              name="lastName"
              type="text"
              component={TextInput}
              label="Last Name"
          />
      </div>

      <div className="row">
      <Field
             name="email"
             type="email" 
             component={TextInput} 
             label="Email"
         />
      </div>

      <div className="row">
      <Field 
             name="password"
             type="password" 
             component={TextInput} 
             label="Password"
       />
      </div>


    <div className="row">
      <label>Gender</label>
    <label>
    <Field 
           name="gender"
           component="input"
           type="radio" 
           value="male"
     /> Male
    </label>
    <label>
    <Field name="gender"
            component="input" 
            type="radio"
             value="female"
     /> Female</label>
    </div>

    <div className="row">
    <label>Favorite Color</label>
    <Field
        name="favoriteColor"
        component="select">
        <option value="color">Select Favorite Color</option>
        <option value="ff0000">Red</option>
        <option value="00ff00">Green</option>
        <option value="0000ff">Blue</option>
    </Field>
    </div>

    <Field
                                            options={[
                                                {
                                                    label: 'Digital Marketing',
                                                    id: 'digital_marketing'
                                                },
                                                {
                                                    label: 'Business Development',
                                                    id: 'business-development'
                                                },
                                                {
                                                    label: 'Market Researcher',
                                                    id: 'market-resercher'
                                                },
                                                {
                                                    label: 'Rearch Analyst',
                                                    id: 'rearch-analyst'
                                                },
                                                {
                                                    label: 'Operations',
                                                    id: 'Operations3'
                                                },
                                                {
                                                    label: 'Digital Marketing',
                                                    id: 'digital_marketing2'
                                                },
                                                {
                                                    label: 'Business Development',
                                                    id: 'business-development3'
                                                },
                                                {
                                                    label: 'Market Researcher',
                                                    id: 'market-resercher2'
                                                },
                                                {
                                                    label: 'Rearch Analyst',
                                                    id: 'rearch-analyst2'
                                                },
                                                {
                                                    label: 'Operations',
                                                    id: 'Operations'
                                                },
                                                {
                                                    label: 'Explicitly remote',
                                                    id: 'header-explicitly-remote'
                                                },
                                            ]}
                                            textColor={theme.colors.textColor}
                                            checkedColor={theme.colors.primaryColor}
                                            component={CheckboxGroup}
                                        />

    <button type="submit" disabled={submitting}>Submit</button>
    <button type="button" disabled={pristine || submitting} onClick={reset}>Clear Values </button>
    </form>
  )
}

export default reduxForm({
      form: 'simple', 
      validate, // form validation 
})(SimpleForm)
