import React from 'react'
import { Field, reduxForm } from 'redux-form';
import { renderField } from './renderField';
import axios from 'axios';
import CheckboxGroup from './CheckboxGroup';



class RegisterPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            person: {
                firstName: '',
                lastName: '',
                email: '',
                password: '',
                gender:'',
                techsKnown:[],
            },
            techs : [{id: 1, name: 'Javascript'}, {id: 2, name: 'Angular'}, {id: 3, name: 'Java'}],
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) =>{
        const {currentTarget:input} = e;
        let s1 = {...this.state};
        input.type === "checkbox" 
        ? input.name === "techsKnown" ? this.updatePart(input.checked,input.value,s1.person.techsKnown)
        : (s1.person[input.name] = input.checked) :
          (s1.person[input.name] = input.value);
        this.setState(s1);
        console.log("data->>>>>",s1);
     }

     updatePart = (checked,value,arr) => {
        if(checked) arr.push(value);
        else{
            let index = arr.findIndex(ele=>ele === value);
            if(index>=0) arr.splice(index,1);
        }
        return arr;
        };

     handleSubmit = async (e) => {
          e.preventDefault();
         let s1 = {...this.state};
        console.log(s1.person);
    }

    render () {
        const { handleSubmit , pristine, reset, submitting,registering,label, required, options, input, meta} = this.props;
        let {firstName,lastName,email,gender,password,techsKnown} = this.state.person;
        const {techs} = this.state;



        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Student Registration</h2>
                <form onSubmit={this.handleSubmit}>
                 <div className="form-group">
                        <Field
                            name="firstName"
                            type="text"
                            value="firstName"
                            label="First Name"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="lastName"
                            type="text"
                            value="lastName"
                            label="Last Name"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="email"
                            type="text"
                            value="email"
                            label="Email"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="password"
                            type="password"
                            value="password"
                            label="Password"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="gender"
                            type="radio"
                            value="male"
                            className="form-control"
                            checked={gender === "Male"}
                            component={renderField}
                            onChange={this.handleChange}
                        />
                         <label className="form-check-label">Male</label>
                    </div>

                    <div className="form-group">
                        <Field
                            name="gender"
                            type="radio"
                            className="form-control"
                            checked={gender === "Female"}
                            component={renderField}
                            onChange={this.handleChange}
                        />
                         <label className="form-check-label">Female</label>
                    </div>

                    <div className="form-group">

                       <Field
                       name="roles"
                       component={CheckboxGroup}
                       options={techs}
                       />
                         <label className="form-check-label">{name}</label>
                    </div>)


                    <button type="submit" disabled={submitting}>Submit</button> { registering && 'Please Wait' }
                    <button type="button" disabled={pristine || submitting} onClick={reset}>Clear Values </button>    
                </form>
            </div>
        );
    }
}

