import React, { useState, useEffect } from 'react'
import styled from "styled-components";
import Tag from './Tag';
import InputError from './inputError';

const CheckboxGroup = (props) => {

    const [options, setOptions] = useState([])

    useEffect(() => {
        setOptions(props.options)
    }, [props.options])

    const onChange = (option) => {
        const { input: { value, onChange } } = props
        let selectedArr = value || []
        let index = findIndex(options, {id: option.id})
        if(options[index] && options[index].is_select){
            options[index].is_select = false
            selectedArr = remove(selectedArr, (item) => {
                return item.id !== option.id;
            });
        }else{
            selectedArr = [...selectedArr, option]
            options[index].is_select = true
        }
        setOptions(options)
        onChange(selectedArr)
    }

    const { label,moreBtnText, meta: { touched, error }} = props  

    return(
        <StyleCheckboxInput style={{color: props.textColor}} checkColor={{checked: props.checkedColor}} className="type-selector">
            <label>{label}</label>
            {options && options.map((option, index) => {
                return <div className="checkbox_groups">
                            <input 
                                type="checkbox"
                                id={option.id}
                                key={index} 
                                onChange={(e) => onChange(option)}
                            />
                            <label htmlFor={option.id}>{option.text}</label> 
                        </div>
            })}
            {touched && (error && <InputError>{error}</InputError>)}
        </StyleCheckboxInput>
    )
}

export default CheckboxGroup

const StyleCheckboxInput = styled.div`
display:flex;
align-items:center;
flex-wrap:wrap;
color: ${props => props.theme.colors.textColor};
.checkbox_groups{
    margin-right:0px;
    input{
        display:none;
        & ~ label{
            padding: 0px 14px;
            color: ${props => props.theme.colors.textColor};
            font-size: 14px;
            font-weight: 400;
            letter-spacing: 0px;
            line-height: 35px;
            text-align: center;
            border-radius:25px;
            height:35px;
            border:1px solid transparent;
            background-color:#f5f5f5;
            outline:none;
            display:inline-block;
            margin-right:15px;
            cursor:pointer;
            &:focus{
                box-shadow:none;
            }
        }
        &:checked{
            & ~ label{
                color:${props => props.style.color};
                background-color:${props => props.checkColor.checked};
            }
        }
    }
}
.show-more-btn{
    color: ${props => props.theme.colors.textColor};
    cursor: pointer;
    font-size:15px;
    cursor:pointer;
}
`;
