import React from 'react';
import { Field, reduxForm } from 'redux-form';


const validate = (values, props) => {
    const errors = {}

    if (isEmpty(values.fullName)) {
        errors.fullName = 'Please enter your full name.';
    } else if (minMaxLenght(values.fullName, 2, 25)) {
        errors.fullName = 'Min 2 and max 20 characters are allowed.';
    } else if (!isChar(values.fullName)) {
        errors.fullName = 'Only alphabets values are allowed.';
    }

    if (isEmpty(values.email)) {
        errors.email = 'Please enter your email.';
    } else if (!isEmail(values.email) || isBlankSpace(values.email)) {
        errors.email = "Please enter valid email."
    }

	if (!values.tel) {
        errors.tel = 'Please enter your telephone number.';
    } else if (minMaxLenght(values.tel, 9, 15)) {
        errors.tel = "Min and Max digits are 9 and 16"
    } else if (!isNumber(values.tel)) {
		errors.tel = "Please enter valid telephone number."
	}

    if (isEmpty(values.password)) {
        errors.password = 'Please enter your password.';
    } else if (minLenght(values.password, 6)) {
        errors.password = 'Password must be at least 6 characters long.';
    }

    if (isEmpty(values.passwordAgain)) {
        errors.passwordAgain = 'Please confirm Password.';
    } else if (values.password !== values.passwordAgain) {
        errors.passwordAgain = 'Password does not match, Please try again.';
    }

	if(props.values.type === "Business"){
		const regEx = /^[a-zA-Z0-9 ]+$/;
		if (isEmpty(values.companyName)) {
			errors.companyName = 'Please enter your company name.';
		} else if (!regEx.test(values.companyName)) {
			errors.companyName = 'Only alphanumeric values are allowed.'
		}
	}

	if(props.values.type === "Graduate"){
		if (isEmpty(values.cv)) {
			errors.cv = 'Please select your CV.';
		} else if (!isEmpty(values.cv) && ["docx", "txt", "doc", "pdf"].indexOf(values.cv.type) === -1) {
			errors.cv = "Unsupported file format."
		}

		if (!values.termsContactConsent) {
			errors.termsContactConsent = 'Please accept our consent checkbox.';
		}
	}

    if (!values.terms) {
        errors.terms = 'Please agree to terms and conditions.';
    }

    return errors;
};


const SimpleForm = props => {
  const { handleSubmit, pristine, reset, submitting } = props;
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>First Name</label>
        <div>
          <Field
            name="firstName"
            component="input"
            type="text"
            placeholder="First Name"
          />
        </div>
      </div>
      <div>
        <label>Last Name</label>
        <div>
          <Field
            name="lastName"
            component="input"
            type="text"
            placeholder="Last Name"
          />
        </div>
      </div>
      <div>
        <label>Email</label>
        <div>
          <Field
            name="email"
            component="input"
            type="email"
            placeholder="Email"
          />
        </div>
      </div>
      <div>
        <label>Sex</label>
        <div>
          <label>
            <Field name="sex" component="input" type="radio" value="male" />
            {' '}
            Male
          </label>
          <label>
            <Field name="sex" component="input" type="radio" value="female" />
            {' '}
            Female
          </label>
        </div>
      </div>
      <div>
        <label>Favorite Color</label>
        <div>
          <Field name="favoriteColor" component="select">
            <option />
            <option value="ff0000">Red</option>
            <option value="00ff00">Green</option>
            <option value="0000ff">Blue</option>
          </Field>
        </div>
      </div>
      <div>
        <label htmlFor="employed">Employed</label>
        <div>
          <Field
            name="employed"
            id="employed"
            component="input"
            type="checkbox"
          />
        </div>
      </div>
      <div>
        <label>Notes</label>
        <div>
          <Field name="notes" component="textarea" />
        </div>
      </div>
      <div>
        <button type="submit" disabled={pristine || submitting}>Submit</button>
        <button type="button" disabled={pristine || submitting} onClick={reset}>
          Clear Values
        </button>
      </div>
    </form>
  );
};

export default reduxForm({
  form: 'simple', // a unique identifier for this form
})(SimpleForm);
