import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';

class SimpleForm extends Component {
  state = {
    person:{
        name:"",
        age:"",
        country:"",
        gender:"",  
        passport:"",
        license:"",
        city:"",
        passport_number: "",
        techsKnown:[],
    },
    countries:[
        "united states of america",
        "India",
        'Australia',
        "Pakistan",
        "United KIngdom"
    ],
    countryList : [
        {
            country:"united states of america",
            cities: ["New york","Los Angles","Seattle","San Francisco"],
        },
        {
            country:"India",
            cities: ["Delhi","Kolkata","Banglore","Mumbai","bihar"],
        },
        {
            country:"Australia",
            cities: ["Melbourne","sydney","Gaba"],
        },
        {
            country:"Pakistan",
            cities: ["Islamabad","sharjha","lahore","MUltan"],
        },
        {
            country:"United KIngdom",
            cities: ["Lords","oval",],
        },

    ],
    techs : ["React","Javascript","Jquery","Angular","vue"],

};
handleChange = (e) =>{
  const {currentTarget:input} = e;
  let s1 = {...this.state};
  input.type === "checkbox" 
  ? input.name === "techsKnown" ? this.updatePart(input.checked,input.value,s1.person.techsKnown)
  : (s1.person[input.name] = input.checked) :
    (s1.person[input.name] = input.value);
