import React from 'react'
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { renderField } from './renderField';


class RegisterPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            user: {
                firstName: '',
                lastName: '',
                email: '',
                password: '',
                gender:'',
                techsKnown:[],
            },
            techs : ["React","Javascript","Jquery","Angular","vue"],
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }



    handleSubmit (e) {
        e.preventDefault();
    }

    render () {
        let {firstName,lastName,email,gender,password,techsKnown} = this.state.user;
        const {techs} = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Register</h2>
                <form onSubmit={this.handleSubmit} >
                    <div className="form-group">
                        <Field
                            name="firstName"
                            type="text"
                            label="First Name"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="lastName"
                            type="text"
                            label="Last Name"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="email"
                            type="text"
                            label="Email"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="password"
                            type="password"
                            label="Password"
                            className="form-control"
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="gender"
                            type="radio"
                            value="male"
                            className="form-control"
                            checked={gender === "Male"}
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="form-group">
                        <Field
                            name="gender"
                            type="radio"
                            className="form-control"
                            checked={gender === "Female"}
                            component={renderField}
                            onChange={this.handleChange}
                        />
                    </div>






                </form>
            </div>
        );
    }
}



export default reduxForm({
      form: 'simple', 
      // validate, // form validation 
})(RegisterPage)
